import React, { useState, useEffect } from 'react';
import { Bell, Search, Calendar, User, AlertCircle, Info, X } from 'lucide-react';
import { AuthController } from '../../controllers/AuthController';
import { Announcement } from '../../models';

// StudentAnnouncementView - Giao diện thông báo cho student (View trong MVC)
const StudentAnnouncementView: React.FC = () => {
  const [announcements, setAnnouncements] = useState<Announcement[]>([]);
  const [filteredAnnouncements, setFilteredAnnouncements] = useState<Announcement[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedAnnouncement, setSelectedAnnouncement] = useState<Announcement | null>(null);
  const [loading, setLoading] = useState(true);

  const currentUser = AuthController.getCurrentUser();

  useEffect(() => {
    loadAnnouncements();
  }, []);

  useEffect(() => {
    filterAnnouncements();
  }, [announcements, searchQuery]);

  const loadAnnouncements = () => {
    setLoading(true);
    
    // Mock announcements data
    const mockAnnouncements: Announcement[] = [
      {
        id: '1',
        title: 'Midterm Exam Schedule',
        content: 'The midterm exams will be held from March 15-20, 2024. Please check your schedule and prepare accordingly. All exams will be conducted in the main examination hall.',
        authorId: '2',
        authorName: 'Dr. Jane Smith',
        targetAudience: 'student',
        priority: 'high',
        publishDate: '2024-01-15',
        isActive: true
      },
      {
        id: '2',
        title: 'Library Hours Extended',
        content: 'The university library will now be open until 10 PM on weekdays to accommodate students preparing for exams. Please make use of the extended hours.',
        authorId: '3',
        authorName: 'Admin',
        targetAudience: 'all',
        priority: 'medium',
        publishDate: '2024-01-14',
        isActive: true
      },
      {
        id: '3',
        title: 'Course Registration Deadline',
        content: 'The deadline for course registration for the Spring semester is January 25, 2024. Please ensure all registrations are completed by this date.',
        authorId: '3',
        authorName: 'Admin',
        targetAudience: 'student',
        priority: 'high',
        publishDate: '2024-01-13',
        isActive: true
      },
      {
        id: '4',
        title: 'Student Council Elections',
        content: 'Student council elections will be held on February 1, 2024. All students are encouraged to participate and vote for their representatives.',
        authorId: '3',
        authorName: 'Admin',
        targetAudience: 'student',
        priority: 'low',
        publishDate: '2024-01-12',
        isActive: true
      }
    ];

    setAnnouncements(mockAnnouncements);
    setLoading(false);
  };

  const filterAnnouncements = () => {
    let filtered = announcements;

    // Filter by search query
    if (searchQuery.trim()) {
      filtered = filtered.filter(announcement =>
        announcement.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        announcement.content.toLowerCase().includes(searchQuery.toLowerCase()) ||
        announcement.authorName.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by target audience (student should see 'all' and 'student' announcements)
    filtered = filtered.filter(announcement =>
      announcement.targetAudience === 'all' || announcement.targetAudience === 'student'
    );

    setFilteredAnnouncements(filtered);
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'text-red-600 bg-red-100';
      case 'medium':
        return 'text-yellow-600 bg-yellow-100';
      case 'low':
        return 'text-green-600 bg-green-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'high':
        return <AlertCircle className="h-4 w-4" />;
      case 'medium':
        return <Info className="h-4 w-4" />;
      case 'low':
        return <Bell className="h-4 w-4" />;
      default:
        return <Bell className="h-4 w-4" />;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const handleAnnouncementClick = (announcement: Announcement) => {
    setSelectedAnnouncement(announcement);
  };

  const closeModal = () => {
    setSelectedAnnouncement(null);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm border p-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">Announcements</h1>
        <p className="text-gray-600">
          Stay updated with the latest announcements from your lecturers and administration.
        </p>
      </div>

      {/* Search Bar */}
      <div className="bg-white rounded-lg shadow-sm border p-6">
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <Search className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            placeholder="Search announcements..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
          />
        </div>
      </div>

      {/* Announcements List */}
      <div className="bg-white rounded-lg shadow-sm border">
        <div className="p-6 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">
            Recent Announcements ({filteredAnnouncements.length})
          </h2>
        </div>
        <div className="p-6">
          {filteredAnnouncements.length === 0 ? (
            <div className="text-center py-8">
              <Bell className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No announcements found</h3>
              <p className="mt-1 text-sm text-gray-500">
                {searchQuery ? 'Try adjusting your search terms.' : 'Check back later for updates.'}
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {filteredAnnouncements.map((announcement) => (
                <div
                  key={announcement.id}
                  onClick={() => handleAnnouncementClick(announcement)}
                  className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50 cursor-pointer transition-colors"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <div className={`p-1 rounded-full ${getPriorityColor(announcement.priority)}`}>
                          {getPriorityIcon(announcement.priority)}
                        </div>
                        <h3 className="text-sm font-medium text-gray-900">
                          {announcement.title}
                        </h3>
                      </div>
                      <p className="text-sm text-gray-600 line-clamp-2 mb-3">
                        {announcement.content}
                      </p>
                      <div className="flex items-center space-x-4 text-xs text-gray-500">
                        <div className="flex items-center space-x-1">
                          <User className="h-3 w-3" />
                          <span>{announcement.authorName}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Calendar className="h-3 w-3" />
                          <span>{formatDate(announcement.publishDate)}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Announcement Detail Modal */}
      {selectedAnnouncement && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-3">
                <div className={`p-2 rounded-full ${getPriorityColor(selectedAnnouncement.priority)}`}>
                  {getPriorityIcon(selectedAnnouncement.priority)}
                </div>
                <h3 className="text-lg font-semibold text-gray-900">
                  {selectedAnnouncement.title}
                </h3>
              </div>
              <button
                onClick={closeModal}
                className="text-gray-400 hover:text-gray-600"
                title="Close"
              >
                <X className="h-5 w-5" />
              </button>
            </div>
            
            <div className="space-y-4">
              <div className="flex items-center space-x-4 text-sm text-gray-500 border-b border-gray-200 pb-4">
                <div className="flex items-center space-x-1">
                  <User className="h-4 w-4" />
                  <span>By {selectedAnnouncement.authorName}</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Calendar className="h-4 w-4" />
                  <span>Published on {formatDate(selectedAnnouncement.publishDate)}</span>
                </div>
                <div className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(selectedAnnouncement.priority)}`}>
                  {selectedAnnouncement.priority.charAt(0).toUpperCase() + selectedAnnouncement.priority.slice(1)} Priority
                </div>
              </div>
              
              <div className="prose prose-sm max-w-none">
                <p className="text-gray-700 leading-relaxed whitespace-pre-wrap">
                  {selectedAnnouncement.content}
                </p>
              </div>
            </div>
            
            <div className="mt-6 flex justify-end">
              <button
                onClick={closeModal}
                className="px-4 py-2 bg-gray-600 text-white rounded-md text-sm font-medium hover:bg-gray-700"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudentAnnouncementView;
