import React from 'react';

// LecturerAnnouncementView - Giao diện lecturerannouncement cho admin (View trong MVC)
const LecturerAnnouncementView: React.FC = () => {
  return (
    <div className="max-w-6xl mx-auto">
      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Announcements</h1>
        <p className="text-gray-600">Create and manage announcements for your students</p>
      </div>

      <div className="card">
        <div className="text-center py-12">
          <h3 className="text-lg font-medium text-gray-900 mb-2">Announcement Management</h3>
          <p className="text-gray-600">This page will allow lecturers to create new announcements and manage existing ones.</p>
        </div>
      </div>
    </div>
  );
};

export default LecturerAnnouncementView;
