import React from 'react';

// AdminCoursesView - Giao diện admincourses cho admin (View trong MVC)
const AdminCoursesView: React.FC = () => {
  return (
    <div className="max-w-6xl mx-auto">
      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Course Management</h1>
        <p className="text-gray-600">Add, edit, and manage course offerings</p>
      </div>

      <div className="card">
        <div className="text-center py-12">
          <h3 className="text-lg font-medium text-gray-900 mb-2">Course Catalog</h3>
          <p className="text-gray-600">This page will allow admins to manage courses with semester filtering and CRUD operations.</p>
        </div>
      </div>
    </div>
  );
};

export default AdminCoursesView;
